mapper.select:
select
	`id`,`cat_name`,`book_name`,`author_name`,`book_desc`,`visit_count`,
	`word_count`,`comment_count` from book

   and id = #{id} 
   and cat_name = #{catName} 
   and book_name = #{bookName} 
   and author_name = #{authorName} 

order by id desc

	limit ${offset}, ${limit}

select count(*) from book

	 and id = #{id} 
	 and cat_name = #{catName} 
	 and book_name = #{bookName} 
	 and author_name = #{authorName}
